import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';

class BoardButton extends React.Component
{
  constructor(props)
  {
    super(props);

    this.state = {count: 0};
  }
  render() 
  {
    return (
      <button id={this.props.id} onClick={() => this.setState({count: (++this.state.count)})}>
        This button was clicked {this.state.count} times
      </button>
    );
  }

  componentDidMount() 
  {
    alert("The button was just introduced ==>" + document.getElementById('btn'));
  }
}

ReactDOM.render(
  <div>
    <BoardButton id='btn'/>
  </div>
  ,
  document.getElementById('root')
);

// If you want to start measuring performance in your app, pass a function
